mapper.update:
UPDATE gallery
SET g_readcnt = g_readcnt + 1
WHERE g_seq = ${g_seq}

UPDATE Tag
SET t_readcnt = t_readcnt + 1
WHERE g_seq = ${g_seq}

UPDATE files
SET f_downcnt = f_downcnt + 1
WHERE f_seq = #{f_seq}

mapper.select:
SELECT *
FROM files
WHERE g_seq = ${g_seq}
ORDER BY f_seq;

mapper.update:
UPDATE gallery
SET g_title = #{g_title} 
   ,g_content = #{g_content}
   ,g_tag = #{g_tag}
WHERE g_seq = #{g_seq} AND g_writer = #{g_writer}
