mapper.insert:
INSERT INTO menu(
			menu_id
		   ,menu_name
		   ,menu_create_user
		   ,menu_create_dttm
		   ,menu_auth
		   ,menu_info
		   ,menu_url
		   ,menu_level
		   ,menu_depth1

				   ,menu_depth2

				   ,menu_depth3


		)
VALUES (
		   #{menu_id}
	      ,#{menu_name}
	      ,#{menu_create_user}
	      ,sysdate()
	      ,#{menu_auth}
	      ,#{menu_info}
	      ,#{menu_url}

	      	,1
	      	,#{menu_id}


	      	,(SELECT menu_level + 1 FROM menu m WHERE menu_id = #{menu_ref})
	        ,(SELECT menu_depth1 FROM menu m WHERE menu_id = #{menu_ref})

			   ,#{menu_id}


			   ,(SELECT menu_depth2 FROM menu m WHERE menu_id = #{menu_ref})
			   ,#{menu_id}


		)

mapper.update:
UPDATE menu	
SET menu_name = #{menu_name}
   ,menu_auth = #{menu_auth}
   ,menu_url = #{menu_url}
   ,menu_info = #{menu_info}
   ,menu_modify_user = #{menu_modify_user}
   ,menu_modify_dttm = sysdate()
WHERE menu_id = #{menu_id}

UPDATE menu	
SET menu_auth = #{menu_auth}
WHERE menu_depth#{menu_level} = #{menu_id}

mapper.delete:
DELETE FROM menu
WHERE menu_id = #{menu_id}

		OR menu_depth#{menu_level} = #{menu_id}
